mapper.select:
select *
from(
	select @rownum:=@rownum+1 rownum, m.*
	from message m, (select @rownum:=0) as tmp
	where recv_id=#{param1} and recv_chk=0
	order by mno desc
) as messagelist

where rownum>(#{param2}-1)*#{param3} and rownum<=#{param2}*#{param3}

select *
from(
	select @rownum:=@rownum+1 rownum, m.*
	from message m, (select @rownum:=0) as tmp
	where send_id=#{param1} and send_chk=0
	order by mno desc
) as messagelist

where rownum>(#{param2}-1)*#{param3} and rownum<=#{param2}*#{param3}

select * 
from(
	select @rownum:=@rownum+1 rownum, m.*
	from message m, (select @rownum:=0) as tmp
	where recv_id=#{param1} and new_msg=0
	order by mno desc
) as messagelist

where rownum>(#{param2}-1)*#{param3} and rownum<=#{param2}*#{param3}

mapper.update:
update message
set recv_chk = 1, new_msg = 1
where mno = #{mno}

update message
set send_chk = 1
where mno = #{mno}

update message
set new_msg = 1
where mno = #{mno}
